using System;using System.Runtime.InteropServices;using System.Xml.Schema;using baithi.controller;using baithi.entity;namespace baithi{    class Program    {        static void Main(string[] args)        {            while (true)            {                Console.WriteLine("-----------Menu Product----------");                Console.WriteLine("1. Add product records.");                Console.WriteLine("2. Display product records.");                Console.WriteLine("3. Delete product by Id.");                Console.WriteLine("4. Exit.");                Console.WriteLine(" pls enter your choice.");                Console.WriteLine("---------------------------------");                var choice = Int32.Parse(Console.ReadLine());                switch (choice)                {                        case 1:                            Console.WriteLine("Add new product.");                            ProductController.addProduct();                                                      break;                        case 2:                            Console.WriteLine("Display product records.");                            ProductController.DisplayList();                            break;                        case 3:                            Console.WriteLine("Delete product by Id.");                            ProductController.DeleteById();                            break;                        case 4:                            Console.WriteLine("Exit.");                            Environment.Exit(0);                            break;                }            }        }    }}