using System;using System.Collections.Generic;using System.Globalization;using baithi.entity;namespace baithi.controller{    public class ProductController    {        private static List<Product> listProduct = new List<Product>();        public static bool addProduct()        {            var product = new Product();            Console.WriteLine("Enter Id product: ");            product.ProductId = Console.ReadLine();            Console.WriteLine("Enter name product:  ");            product.ProductName = Console.ReadLine();            Console.WriteLine("Enter price:  ");            product.Price = Console.ReadLine();                        listProduct.Add(product);            return true;                    }        public static void DisplayList()        {            Console.WriteLine("ok");            foreach (var product in listProduct)            {                Console.WriteLine("{0, -20} {1,-20} {2, -20} ", "Product Id", "Product Name", "Price");                Console.WriteLine("----------------------------------------------------------------------");                Console.WriteLine("{0, -20} {1, -20} {2, -20}", product.ProductId, product.ProductName, product.Price);            }            Console.ReadLine();        }        public static void DeleteById()        {            Console.WriteLine("pls enter Id to delete: ");            var id = Console.ReadLine();            Product delete = listProduct.Find(a => a.ProductId == id);            listProduct.Remove(delete);        }    }}